SELECT
    catalogo_bien_id, codigo_bien, denominacion, grupo_id, 
    clase_id, grupo, clase, dt_marca, 
    dt_modelo, dt_tipo, dt_dimension, dt_color, 
    dt_serie, dt_anio, dt_placa, dt_raza, 
    dt_motor, dt_nombre, dt_chasis, dt_edad, 
    dt_btn_otro, ind_del, fec_reg, fec_act, 
    usu_reg, usu_act
FROM 
    inventario.catalogo_bien


         AND catalogo_bien_id = #{catalogoBienID,jdbcType=INTEGER}


         AND codigo_bien = #{codigoBien,jdbcType=VARCHAR}


         AND denominacion = #{denominacion,jdbcType=VARCHAR}


         AND grupo_id = #{grupoID,jdbcType=INTEGER}


         AND clase_id = #{claseID,jdbcType=INTEGER}


         AND grupo = #{grupo,jdbcType=VARCHAR}


         AND clase = #{clase,jdbcType=VARCHAR}


         AND dt_marca = #{dtMarca,jdbcType=VARCHAR}


         AND dt_modelo = #{dtModelo,jdbcType=VARCHAR}


         AND dt_tipo = #{dtTipo,jdbcType=VARCHAR}


         AND dt_dimension = #{dtDimension,jdbcType=VARCHAR}


         AND dt_color = #{dtColor,jdbcType=VARCHAR}


         AND dt_serie = #{dtSerie,jdbcType=VARCHAR}


         AND dt_anio = #{dtAnio,jdbcType=VARCHAR}


         AND dt_placa = #{dtPlaca,jdbcType=VARCHAR}


         AND dt_raza = #{dtRaza,jdbcType=VARCHAR}


         AND dt_motor = #{dtMotor,jdbcType=VARCHAR}


         AND dt_nombre = #{dtNombre,jdbcType=VARCHAR}


         AND dt_chasis = #{dtChasis,jdbcType=VARCHAR}


         AND dt_edad = #{dtEdad,jdbcType=VARCHAR}


         AND dt_btn_otro = #{dtBtnOtro,jdbcType=VARCHAR}


         AND ind_del = #{indDel,jdbcType=VARCHAR}


         AND fec_reg = #{fechaReg,jdbcType=TIMESTAMP}


         AND fec_act = #{fechaAct,jdbcType=TIMESTAMP}


         AND usu_reg = #{usuReg,jdbcType=VARCHAR}


         AND usu_act = #{usuAct,jdbcType=VARCHAR}

SELECT
