SELECT i.inventario_bien_id as "inventarioBienID", i.bien_id as "bienID", i.inventario_id as "inventarioID", (select inv.nombre from inventario inv where inv.inventario_id = i.inventario_id) as "inventarioDescrip", i.estado_subida as "estadoSubida", (select c.descripcion from catalogo c where c.catalogo = 'ESTADO_SUBIDA' and c.ind_del = '0' and c.tipo = 'DET' and c.datacatalogo = i.estado_subida) as "estadoSubidaDescrip", i.sobrante_faltante as "sobranteFaltante", (select c.descripcion from catalogo c where c.catalogo = 'SOBRANTE_FALTANTE' and c.ind_del = '0' and c.tipo = 'DET' and c.datacatalogo = i.sobrante_faltante) as "sobranteFaltanteDescrip", i.inventariador as "inventariador", (select CONCAT( CONCAT(u.nombres, ' '), u.apellidos) from usuario u where u.usuario_id = i.inventariador ) as "inventariadorDescrip", (select CONCAT( CONCAT(u.nombres, ' '), u.apellidos) from usuario u where u.usuario_id = i.inventariador_anterior ) as "inventariadorAnteriorDescrip", i.codigo_patrimonial as "codigoPatrimonial", i.entidad_id as "entidadID", (select e.entidad from inventario.entidad e where i.entidad_id = e.entidad_id) as "entidadDescrip", i.locales_id as "localesID", (select l.nombre_local from inventario.locales l where i.locales_id = l.locales_id) as "localesDescrip", i.area_id as "areaID", (select a.nombre_area from inventario.area a where i.area_id = a.area_id) as "areaDescrip", i.empleado_id as "empleadoID", i.oficina_id as "oficinaID", (select o.nombre_oficina from inventario.oficina o where i.oficina_id = o.oficina_id) as "oficinaDescrip", i.tipo_causal_alta as "tipoCausalAlta", i.anho as "anho", i.matricula as "matricula", i.ancho as "ancho", i.valor_libro as "valorLibro", i.cuenta_con_seguro as "cuentaConSeguro", i.estado_bien as "estadoBien", i.dimension as "dimension", i.condicion as "condicion", i.nro_doc_adquisicion as "numeroDocAdquisicion", i.color as "color", i.fecha_adquisicion as "fechaAdquisicion", i.ent_disp as "entDisp", i.ent_arre as "entArre", i.fe