SELECT
    locales_id  as "id", upper(nombre_local) as "label",
    entidad_id as "codEntidad" 
FROM 
    inventario.locales


         AND locales_id = #{localesID,jdbcType=INTEGER}


         AND codigo = #{codigo,jdbcType=VARCHAR}


         AND entidad_id = #{entidadID,jdbcType=INTEGER}


         AND nombre_local = #{nombreLocal,jdbcType=VARCHAR}


         AND direccion = #{direccion,jdbcType=VARCHAR}


         AND dsc_mz = #{dscMz,jdbcType=VARCHAR}


         AND dsc_tomo = #{dscTomo,jdbcType=VARCHAR}


         AND ctd_area = #{ctdArea,jdbcType=DECIMAL}


         AND dsc_fojas = #{dscFojas,jdbcType=INTEGER}


         AND cod_ubigeo = #{codigoUbigeo,jdbcType=VARCHAR}


         AND flg_propie = #{flgPropie,jdbcType=VARCHAR}


         AND dsc_benefi = #{dscBenefi,jdbcType=VARCHAR}


         AND dsc_asinab = #{dscAsinab,jdbcType=VARCHAR}


         AND dsc_pelect = #{dscPelect,jdbcType=VARCHAR}


         AND flg_tipovi = #{flgTipovi,jdbcType=VARCHAR}


         AND flg_tipore = #{flgTipore,jdbcType=VARCHAR}


         AND dsc_ctactb = #{dscCtactb,jdbcType=VARCHAR}


         AND dsc_asient = #{dscAsient,jdbcType=VARCHAR}


         AND cod_predio = #{codigoPredio,jdbcType=VARCHAR}


         AND ctd_valor = #{ctdValor,jdbcType=DECIMAL}


         AND flg_um = #{flgUm,jdbcType=VARCHAR}


         AND num_mun = #{numeroMun,jdbcType=INTEGER}


         AND dsc_lote = #{dscLote,jdbcType=VARCHAR}


         AND dsc_propie = #{dscPropie,jdbcType=VARCHAR}


         AND flg_tipomo = #{flgTipomo,jdbcType=VARCHAR}


         AND ind_del = #{indDel,jdbcType=VARCHAR}


         AND fec_reg = #{fechaReg,jdbcType=DATE}


         AND fec_act = #{fechaAct,jdbcType=DATE}


         AND usu_reg = #{usuReg,jdbcType=VARCHAR}


         AND usu_act = #{usuAct,jdbcType=VARCHAR}

SELECT IFNULL( MAX(CONVERT(l.codigo,SIGNED INTEGER)), 0 ) as codigo
FROM inventario.locales l
WHERE l.ind_del = #{indDel,jdbcType=VARCHAR} AND l.entidad_id = #{entidadID,jdbcType=INTEGER}
